/* eslint-disable react-hooks/rules-of-hooks */
import React, { Component } from 'react';

//const [text, setText] = React.useState('');    
function ChangeQuantity_keyDown(id) {    
    // let selectQuantityStr = '#' + id + ' .Quantity';
    // let selectPriceStr = '#' + id + ' .Price';
    // let QuantityEle = document.querySelector(selectQuantityStr);
    // let PriceEle = document.querySelector(selectPriceStr);
    // let totalPrice = document.querySelector('#cal .total-price');
    // let total = document.querySelector('#cal .total-pay');       
    // let totalQuantity = document.querySelector('#cal .total-quantity');
    // let temp = parseFloat(PriceEle.value) * parseInt(QuantityEle.value)
    // totalPrice.value = parseFloat(totalPrice.value) - temp;
    // total.value = parseFloat(total.value) - temp;
}

function ChangeQuantity_keyUp(id, input) {  
    // let string = '#' + id + ' .Quantity';
    // let Quantity = document.querySelector(string);  
    // if(input.key >= 0 && input.key <= 9) {
        
    // console.log(Quantity.value);
    // }
    var activeElement = document.activeElement;
    if(activeElement.tagName === "INPUT" && activeElement.type === "text") {
      var value = activeElement.value;
      if(value >= 0 && value <= 9) {
        // Hành động cần thực hiện khi phím số được nhấn
        console.log("van");
      }
    }
    let totalPrice = document.querySelector('#cal .total-price');
    let total = document.querySelector('#cal .total-pay');       
    let totalQuantity = document.querySelector('#cal .total-quantity');
}


class printProduct extends Component {
    render() {
        return (
            <div className="box-product">             
                <img className="Picture" src={this.props.Link} />
                <label className="Name">{this.props.Name}</label>
                <input className="Quantity" type="number" defaultValue={this.props.Quantity} id={this.props.id} onKeyUp={e => ChangeQuantity_keyUp(e.target.id, this)} onKeyDown={e => ChangeQuantity_keyDown(e.target.id)}/>
                <input readOnly className="Price" defaultValue={this.props.Price}></input>
                <button className="Delete"><i className="fa-sharp fa-solid fa-trash" /></button>
          </div>
        );
    }
}
export default printProduct;
