window.addEventListener('beforeunload', function() {
    localStorage.clear();    
    const Obj = {check : false}; 
    localStorage.setItem("checkDiscount", JSON.stringify(Obj));
    localStorage.setItem("myProducts", JSON.stringify([]));
    localStorage.setItem("myCustomer", JSON.stringify({}));
});
const selectClient = () => { 
    const text = sessionStorage.getItem("Employee");
    return (            
        <div className="find-client">
            <input type="text" className="input-box-client" style={{paddingLeft: '40px'}} value={text} readOnly/>
      </div>
    );
} 
export default selectClient
