                    <Warning content='Vượt quá số lượng tồn kho !!!' name_class='warningBox' ></Warning>
                </React.StrictMode>
            )
        }
    } 
}

const pay = () => {  
    return (                  
        <button className="pay" onClick={() => addBill()}>THANH TOÁN</button>
    );
} 
